package main

import (
	"encoding/json"
	"fmt"
)

func main() {

	jsonrc := "{\"POSTGL3cbcd4f5763337c93b2c0f95c29b1f7f51d0d71d28a8bd9e3fc36104819065dfPOB08010129091IOF A PAGAR\":\"\",\"POSTGL3cbcd4f5763337c93b2c0f95c29b1f7f51d0d71d28a8bd9e3fc36104819065dfPOB08010129091PREMIO A RECEBER\":\"\",\"POSTGL3cbcd4f5763337c93b2c0f95c29b1f7f51d0d71d28a8bd9e3fc36104819065dfPOB08010129091PREMIO EMITIDO\":\"\"}"
	b := []byte(jsonrc)

	m := make(map[string]string)

	err := json.Unmarshal(b, &m)
	if err != nil {
		fmt.Println(err)
	}

	fmt.Println(m)

}
